mapper.update:
update tbl_board set title=#{title},
content=#{content}, writer=#{writer}, updatedate=now()
where bno=#{bno}

update tbl_board set hitcount = hitcount+1
where
bno=#{bno}

mapper.select:
select * from tbl_board where bno>0
order by bno desc
limit #{pageStart},#{pageSize}

